// select all divs and give them a purple bg
$('div').css('background-color', 'purple');

// select the divs with the class "highlight" and make them 200px wide
$('.highlight').css('width', '200px');

// select the third div with id "third" and give it a orange border
$('#third').css('border', '1px solid orange');

// select the first div only and change its color to pink
$('div:first-child').css('color', 'pink');
